import React, { useEffect, useState } from 'react';
import axios from 'axios';

const BASE_URL = 'http://localhost:5000';

function Post() {
    const [posts, setPosts] = useState([]);
    const [comments, setComments] = useState([]);
    const [error, setError] = useState(null);
    const [newPost, setNewPost] = useState({
        title: '', description: '', userId: '1'
    });
    const [editPost, setEditPost] = useState(null);
    const [newComment, setNewComment] = useState({
        text: '', userId: '1'
    });
    const [showPostDetail, setShowPostDetail] = useState(false); // To toggle the post detail modal
    const [currentPostId, setCurrentPostId] = useState(null); // To store the current post ID for the details modal

    // Fetch posts when component mounts
    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await axios.get(`${BASE_URL}/posts`);
                setPosts(response.data);
            } catch (err) {
                setError('Failed to fetch posts');
                console.error(err);
            }
        };
        fetchPosts();
    }, []);

    // Fetch comments for a specific post
    const fetchComments = async (postId) => {
        try {
            const response = await axios.get(`${BASE_URL}/comments?postId=${postId}`);
            setComments(response.data);
        } catch (err) {
            setError('Failed to fetch comments');
            console.error(err);
        }
    };

    // Handle opening of the post details modal
    const handlePostDetail = (postId) => {
        setCurrentPostId(postId);
        fetchComments(postId); // Fetch comments for the specific post
        setShowPostDetail(true); // Show the modal
    };

    // Close the post details modal
    const closePostDetail = () => {
        setShowPostDetail(false);
        setComments([]); // Clear comments when modal is closed
    };

    // Create a new post
    const handleCreatePost = async () => {
        const now = new Date().toISOString(); // Get current timestamp

        try {
            const response = await axios.post(`${BASE_URL}/posts`, {
                ...newPost,
                createdAt: now,
                updatedAt: now, // Set both createdAt and updatedAt to current time
            });

            setPosts([...posts, response.data]);
            setNewPost({ title: '', description: '', userId: '1' });
        } catch (err) {
            setError('Failed to create post');
            console.error(err);
        }
    };

    // Edit a post
    const handleEditPost = async () => {
        if (!editPost) return;
        try {
            const updatedPost = { ...editPost, updatedAt: new Date().toISOString() };
            const response = await axios.put(`${BASE_URL}/posts/${editPost.id}`, updatedPost);
            setPosts(posts.map(post => post.id === editPost.id ? response.data : post));
            setEditPost(null);
        } catch (err) {
            setError('Failed to update post');
            console.error(err);
        }
    };

    // Delete a post
    const handleDeletePost = async (postId) => {
        try {
            await axios.delete(`${BASE_URL}/posts/${postId}`);
            setPosts(posts.filter(post => post.id !== postId));
            setShowPostDetail(false); // Close the modal when post is deleted
        } catch (err) {
            setError('Failed to delete post');
            console.error(err);
        }
    };

    // Create a comment for a post
    const handleCreateComment = async () => {
        if (!newComment.text) {
            setError('Comment is required');
            return;
        }

        const now = new Date().toISOString();

        try {
            const response = await axios.post(`${BASE_URL}/comments?postId=${currentPostId}`, {
                ...newComment,
                createdAt: now,
                updatedAt: now, // Set both createdAt and updatedAt to current time
            });

            fetchComments(currentPostId);
            setNewComment({ text: '', userId: '1' });
        } catch (err) {
            setError('Failed to create comment');
            console.error(err);
        }
    };

    // Delete a comment
    const handleDeleteComment = async (commentId) => {
        try {
            await axios.delete(`${BASE_URL}/comments?postId=${currentPostId}&commentId=${commentId}`);
            fetchComments(currentPostId);
        } catch (err) {
            setError('Failed to delete comment');
            console.error(err);
        }
    };

    return (
        <div>
            {error && <div>{error}</div>}

            {/* Create Post Form */}
            <div>
                <h3>Create Post</h3>
                <input
                    type="text"
                    name="title"
                    placeholder="Title"
                    value={newPost.title}
                    onChange={(e) => setNewPost({ ...newPost, title: e.target.value })}
                />
                <input
                    type="text"
                    name="description"
                    placeholder="Description"
                    value={newPost.description}
                    onChange={(e) => setNewPost({ ...newPost, description: e.target.value })}
                />
                <button onClick={handleCreatePost}>Create</button>
            </div>

            {/* Display Posts */}
            {posts.length > 0 ? (
                <table border="1" cellPadding="10" cellSpacing="0">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Created At</th>
                            <th>Updated At</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {posts.map((post) => (
                            <tr key={post.id}>
                                <td>{post.id}</td>
                                <td>{post.title}</td>
                                <td>{post.description}</td>
                                <td>{new Date(post.createdAt).toLocaleString()}</td>
                                <td>{new Date(post.updatedAt).toLocaleString()}</td>
                                <td>
                                    <button onClick={() => handlePostDetail(post.id)}>Post Detail</button>
                                    <button onClick={() => setEditPost(post)}>Edit</button>
                                    <button onClick={() => handleDeletePost(post.id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <div>No posts available</div>
            )}

            {/* Modal for Post Details, Edit and Comments */}
            {showPostDetail && (
                <div className="modal">
                    <div className="modal-content">
                        <span className="close" onClick={closePostDetail}>&times;</span>
                        <h3>Post Details</h3>
                        {/* Post Edit Form */}
                        <div>
                            <input
                                type="text"
                                name="title"
                                placeholder="Title"
                                value={editPost?.title || ''}
                                onChange={(e) => setEditPost({ ...editPost, title: e.target.value })}
                            />
                            <input
                                type="text"
                                name="description"
                                placeholder="Description"
                                value={editPost?.description || ''}
                                onChange={(e) => setEditPost({ ...editPost, description: e.target.value })}
                            />
                            <button onClick={handleEditPost}>Update Post</button>
                        </div>

                        <p><strong>Created At:</strong> {new Date(editPost?.createdAt).toLocaleString()}</p>
                        <p><strong>Updated At:</strong> {new Date(editPost?.updatedAt).toLocaleString()}</p>

                        <h4>Comments</h4>
                        <table border="1" cellPadding="10" cellSpacing="0">
                            <thead>
                                <tr>
                                    <th>Text</th>
                                    <th>User ID</th>
                                    <th>Created At</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {comments.length > 0 ? (
                                    comments.map((comment) => (
                                        <tr key={comment.id}>
                                            <td>{comment.text}</td>
                                            <td>{comment.userId}</td>
                                            <td>{new Date(comment.createdAt).toLocaleString()}</td>
                                            <td>
                                                <button onClick={() => handleDeleteComment(comment.id)}>Delete</button>
                                            </td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr><td colSpan="4">No comments yet</td></tr>
                                )}
                            </tbody>
                        </table>

                        {/* Create Comment Form */}
                        <div>
                            <h4>Create Comment</h4>
                            <input
                                type="text"
                                name="text"
                                placeholder="Enter your comment"
                                value={newComment.text}
                                onChange={(e) => setNewComment({ ...newComment, text: e.target.value })}
                            />
                            <button onClick={handleCreateComment}>Create Comment</button>
                        </div>

                        {/* Delete Post Button */}
                        <div>
                            <button onClick={() => handleDeletePost(editPost?.id)}>Delete Post</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Post;
