import React, { useEffect, useState } from 'react';
import axios from 'axios';

const BASE_URL = 'http://localhost:5000';

function Post() {
    const [posts, setPosts] = useState([]);
    const [comments, setComments] = useState([]); // Store comments for a specific post
    const [error, setError] = useState(null);
    const [newPost, setNewPost] = useState({
        title: '', description: '', userId: '1', createdAt: '', updatedAt: ''
    });
    const [editPost, setEditPost] = useState(null);
    const [newComment, setNewComment] = useState({
        text: '', userId: '1', createdAt: '', updatedAt: ''
    });

    // Fetch posts when component mounts
    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await axios.get(`${BASE_URL}/posts`);
                setPosts(response.data);
            } catch (err) {
                setError('Failed to fetch posts');
                console.error(err);
            }
        };
        fetchPosts();
    }, []);

    // Fetch comments for a specific post by postId
    const fetchComments = async (postId) => {
        try {
            const response = await axios.get(`${BASE_URL}/posts/${postId}/comments`);
            setComments(response.data); // Set the comments for this specific post
        } catch (err) {
            setError('Failed to fetch comments');
            console.error(err);
        }
    };

    // Handle creating a post
    const handleCreatePost = async () => {
        try {
            const response = await axios.post(`${BASE_URL}/posts`, newPost);
            setPosts([...posts, response.data]);
            setNewPost({
                title: '', description: '', userId: '1', createdAt: '', updatedAt: ''
            });
        } catch (err) {
            setError('Failed to create post');
            console.error(err);
        }
    };

    // Handle editing a post
    const handleUpdatePost = async () => {
        if (!editPost) return;
        try {
            const response = await axios.put(`${BASE_URL}/posts/${editPost.id}`, editPost);
            setPosts(posts.map(post => post.id === editPost.id ? response.data : post));
            setEditPost(null); // Clear the edit form after update
        } catch (err) {
            setError('Failed to update post');
            console.error(err);
        }
    };

    // Handle deleting a post
    const handleDeletePost = async (postId) => {
        try {
            await axios.delete(`${BASE_URL}/posts/${postId}`);
            setPosts(posts.filter(post => post.id !== postId));
        } catch (err) {
            setError('Failed to delete post');
            console.error(err);
        }
    };

    // Handle creating a comment
    const handleCreateComment = async (postId) => {
        if (!newComment.text) {
            setError('Comment is required');
            return;
        }

        try {
            const response = await axios.post(`${BASE_URL}/posts/${postId}/comments`, newComment);
            fetchComments(postId); // Refresh comments for the current post
            setNewComment({
                text: '', userId: '1', createdAt: '', updatedAt: ''
            });
        } catch (err) {
            setError('Failed to create comment');
            console.error(err);
        }
    };

    // Handle deleting a comment
    const handleDeleteComment = async (postId, commentId) => {
        try {
            await axios.delete(`${BASE_URL}/posts/${postId}/comments/${commentId}`);
            fetchComments(postId); // Refresh comments after deletion
        } catch (err) {
            setError('Failed to delete comment');
            console.error(err);
        }
    };

    // Handle changes in form input for creating/editing posts
    const handlePostChange = (e) => {
        const { name, value } = e.target;
        if (editPost) {
            setEditPost({ ...editPost, [name]: value });
        } else {
            setNewPost({ ...newPost, [name]: value });
        }
    };

    // Handle changes in form input for creating comments
    const handleCommentChange = (e) => {
        const { name, value } = e.target;
        setNewComment(prev => ({ ...prev, [name]: value }));
    };

    return (
        <div>
            {error && <div>{error}</div>}

            {/* Post Creation Form */}
            <div>
                <h3>Create Post</h3>
                <input
                    type="text"
                    name="title"
                    placeholder="Title"
                    value={newPost.title}
                    onChange={handlePostChange}
                />
                <input
                    type="text"
                    name="description"
                    placeholder="Description"
                    value={newPost.description}
                    onChange={handlePostChange}
                />
                <input
                    type="datetime-local"
                    name="createdAt"
                    value={newPost.createdAt}
                    onChange={handlePostChange}
                />
                <input
                    type="datetime-local"
                    name="updatedAt"
                    value={newPost.updatedAt}
                    onChange={handlePostChange}
                />
                <button onClick={handleCreatePost}>Create</button>
            </div>

            {/* Post Editing Form */}
            {editPost && (
                <div>
                    <h3>Edit Post</h3>
                    <input
                        type="text"
                        name="title"
                        placeholder="Title"
                        value={editPost.title}
                        onChange={handlePostChange}
                    />
                    <input
                        type="text"
                        name="description"
                        placeholder="Description"
                        value={editPost.description}
                        onChange={handlePostChange}
                    />
                    <input
                        type="datetime-local"
                        name="createdAt"
                        value={editPost.createdAt}
                        onChange={handlePostChange}
                    />
                    <input
                        type="datetime-local"
                        name="updatedAt"
                        value={editPost.updatedAt}
                        onChange={handlePostChange}
                    />
                    <button onClick={handleUpdatePost}>Update</button>
                </div>
            )}

            {/* Post Listing */}
            {posts.length > 0 ? (
                <table border="1" cellPadding="10" cellSpacing="0">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Created At</th>
                            <th>Updated At</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {posts.map((post) => (
                            <tr key={post.id}>
                                <td>{post.id}</td>
                                <td>{post.title}</td>
                                <td>{post.description}</td>
                                <td>{new Date(post.createdAt).toLocaleString()}</td>
                                <td>{new Date(post.updatedAt).toLocaleString()}</td>
                                <td>
                                    <button onClick={() => setEditPost(post)}>Edit</button>
                                    <button onClick={() => handleDeletePost(post.id)}>Delete</button>
                                    <button onClick={() => fetchComments(post.id)}>View Comments</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : (
                <div>No posts available</div>
            )}

            {/* Comments for Selected Post */}
            {comments.length > 0 && (
                <div>
                    <h3>Comments</h3>
                    <table border="1" cellPadding="10" cellSpacing="0">
                        <thead>
                            <tr>
                                <th>Text</th>
                                <th>User ID</th>
                                <th>Created At</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {comments.map((comment) => (
                                <tr key={comment.id}>
                                    <td>{comment.text}</td>
                                    <td>{comment.userId}</td>
                                    <td>{new Date(comment.createdAt).toLocaleString()}</td>
                                    <td>
                                        <button onClick={() => handleDeleteComment(posts.id, comment.id)}>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    {/* Comment Creation Form */}
                    <div>
                        <h3>Create Comment</h3>
                        <input
                            type="text"
                            name="text"
                            placeholder="Enter your comment"
                            value={newComment.text}
                            onChange={handleCommentChange}
                        />
                        <input
                            type="datetime-local"
                            name="createdAt"
                            value={newComment.createdAt}
                            onChange={handleCommentChange}
                        />
                        <input
                            type="datetime-local"
                            name="updatedAt"
                            value={newComment.updatedAt}
                            onChange={handleCommentChange}
                        />
                        <button onClick={() => handleCreateComment(posts.id)}>Create Comment</button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Post;
