    <div>
    {error && <div>{error}</div>}

    {posts.length > 0 ? (
        <table border="1" cellPadding="10" cellSpacing="0">
            <thead>
                <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>Description</th>
                    <th>User ID</th>
                    <th>Created At</th>
                    <th>Updated At</th>
                </tr>
            </thead>
            <tbody>
                {post.map((post) => (
                    <tr key={post.id}>
                        <td>{post.id}</td>
                        <td>{post.title}</td>
                        <td>{post.description}</td>
                        <td>{post.userId}</td>
                        <td>{new Date(post.createdAt).toLocaleString()}</td>
                        <td>{new Date(post.updatedAt).toLocaleString()}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    ) : (
        <div>No posts available</div>
    )}
</div>
  );
};

export default PostDetail;
