import React, { useRef, useState, useEffect, useContext } from 'react'
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";


const Login = () => {
    

    const userRef = useRef();
    const errRef = useRef();

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    const usenavigate=useNavigate();


    


    const ProceedLogin = async (e) => {
        e.preventDefault();
        let regobj = { username, password }; // username is the email in your case
        console.log(regobj);
    
        // Use query parameters in the GET request
        fetch(`http://localhost:5000/users?email=${username}&password=${password}`)
            .then((res) => res.json()) // Parse the JSON response
            .then((resp) => {
                console.log(resp);
    
                // Check if response object is empty or not
                if (Object.keys(resp).length === 0) {
                    toast.error('Please enter a valid username or password');
                } else {
                    // Validate the password (although you're already sending the password in the query)
                    if (resp.password === password) {
                        toast.success('Login successful');
                        usenavigate('/');
                    } else {
                        toast.error('Incorrect password');
                    }
                }
            })
            .catch((err) => {
                toast.error('Login failed due to: ' + err.message);
            });
    };
    

   
    
    


    return (
        <>
            {success ? (
                <section>
                    <h1>You are logged in !</h1>
                    <br />
                    <p>
                        <a href="http://localhost:3000/">Go to Home</a>
                    </p>
                </section>
            ) : (
                <section>
                    <p ref={errRef} className={errMsg ? "errmsg" :
                        "offscreen"} aria-live="assertive">{errMsg}</p>
                    <h1>Sign In</h1>
                    <form onSubmit={ProceedLogin}>
                        <label htmlFor="username">Username:</label>
                        <input
                            type="text"
                            id="username"
                            ref={userRef}
                            autoComplete="off"
                            onChange={(e) => setUsername(e.target.value)}
                            value={username}
                            required
                        />

                        <label htmlFor="password">password:</label>
                        <input
                            type="password"
                            id="password"
                            onChange={(e) => setPassword(e.target.value)}
                            value={password}
                            required
                        />
                        <button disabled={!username || !password  ? true : false}>Sign In</button>
                    </form>
                    <p>
                        Need an account? <br />
                        <span className="line">
                           
                            <a href="http://localhost:3000/register">Sign Up</a>
                        </span>
                    </p>
                </section>
            )}

        </>
    )
}

export default Login
